import React, { useState } from 'react';
import Accordion from './components/Accordion';
import Search from './components/Search';
import Dropdown from './components/Dropdown';
import Translate from './components/Tsanslate';
import Route from './components/Route';

const items = [
    {
        title: 'What is react?',
        content: 'React is poutses kitrines'
    },
    {
        title: 'why use react',
        content: 'to change poutses kitrines to mple'
    },
    {
        title: 'poutses mple?',
        content: 'oxi poutses kitrines'
    }
]

const options = [
    {
        label: 'Red',
        value: 'red'
    },
    {
        label: 'Green',
        value: 'green'
    },
    {
        label: 'Blue',
        value: 'blue'
    }
];

const showAccordion = () => {
    if (window.location.pathname === '/')
        return <Accordion items={items} />;
};

const showSearch = () => {
    if (window.location.pathname === '/wikisearch')
        return <Search />;
};

const showDropdown = () => {
    if (window.location.pathname === '/dropdown')
        return <Dropdown />;
};

const showTranslate = () => {
    if (window.location.pathname === '/translate')
        return <Translate />;
};

export default () => {
    const [selected, setSelected] = useState(options)
    return (//  ,  , { showDropdown ?
        // <Dropdown
        //    selected={selected}
        //   onSelectedChange={setSelected}
        //   options={options}
        // /> : null
        // }
        <div>
            <Route path="/"><Accordion items={items} /></Route>
            <Route path="/wikisearch"><Search /></Route>
            <Route path="/dropwdown">
                <Dropdown
                    label="Select a color"
                    options={options}
                    selected={selected}
                    onSelectedChange={setSelected}
                />
            </Route>
            <Route path="/translate"><Translate /></Route>
        </div>
    );
};
