import { useEffect } from 'react';
import useMedia from './useMedia';
import useLocalStorage from './useLocalStorage';

const useDarkMode = () => {
    const [enabledState, setEnabledState] = useLocalStorage('dark-mode-enabled');
    const prefersDarkMode = usePrefersDarkMode();

    const enabled =
        typeof enabledState !== 'undefined' ? enabledState : prefersDarkMode;

    useEffect(
        () => {
            const className = 'dark-mode';
            // const element = window.document.body.children;

            const element = document.getElementById('root')
            if (element) {
                console.log('!!!!!', element);
                if (element.children) {
                    console.log('!!!!!', element.children);
                    for (let i = 0; i < element.children[i].children.length; i++) {
                        for (let i = 0; i < element.children[i].length; i++) {
                            console.log('???????', element[i]);
                            if (enabled) {
                                element.children[i].classList.add(className);
                            } else {
                                element.children[i].classList.remove(className);
                            }
                        }
                    }
                }

            }
        },
        [enabled]
    );
    return [enabled, setEnabledState];
}

const usePrefersDarkMode = () => {
    return useMedia(['(prefers-color-scheme: dark)'], [true], false);
}

export default useDarkMode;
